    public void giveLives() {
        int w;
        if (score >= 1000) {
            w = floor(score / 1000);
            for (int i = livesClaimed; i < livesOrder.length; i++) {
                if (w >= livesOrder[i]) {
                    if (livesClaimed < i + 1) {
                        lives++;
                        messages = splice(messages, "Claimed life:" + str(livesOrder[i])+"000", 0);
                        livesClaimed++;
                        playSound(extra_life);
                    }
                }
            }
        }
    }

