    public String makeDir(int Var) {
        return switch (Var) {
            case 0 -> "up";
            case 1 -> "down";
            case 2 -> "right";
            case 3 -> "left";
            default -> "stopped";
        };
    }

    public int makeDirNum() {
        float tempDirNum = random(-0.1f, 4);
        int dirNum = floor(tempDirNum);
        dirNum = constrain(dirNum, 0, 3);
        return dirNum;
    }

    public void createMaze() {
        for (int i = 0; i < cellMap[1].length + 1; i++) {
            for (int j = 0; j < cellMap[1].length + 1; j++) {
                cells[i][j] = new Cell(Math.round(i * cellWidth), Math.round(j * cellWidth));
            }
        }
    }

    public void initializeMaze() {
        for (Cell[] cell : cells) {
            cell[0].open = false;
            cell[cells.length - 1].open = false;
        }
        for (int i = 0; i < cells[0].length; i++) {
            cells[0][i].open = false;
            cells[cells.length - 1][i].open = false;
        }
        for (int row = 1; row < cellMap.length + 1; row++) {
            for (int col = 0; col < cellMap[row - 1].length; col++) {
                cells[col][row].open = cellMap[row - 1][col];
                if (cellMap[row - 1][col]) {
                    //noinspection StatementWithEmptyBody
                    if (col == 1 && row == 1) {
                    } else {
                        pellet = (Pellet[]) append(pellet, new Pellet(Math.round(col * cellWidth + cellWidth / 2), Math.round(row * cellWidth + cellWidth / 2)));
                        coords2 = (Coordinate[]) append(coords2, new Coordinate());
                        cellCount++;
                    }
                }
            }
        }
    }

    public void showMaze(int mazeColor, boolean all) {
        for (Cell[] cell : cells) {
            for (Cell value : cell) {
                value.show(mazeColor, all);
            }
        }
    }

