    //// Pellet \\\\
    class Pellet {
        int x, y;
        boolean eaten = false, isFruit = false;
        String fruitType = "cherry";

        Pellet(int x1, int y1) {
            x = x1;
            y = y1;
        }

        public void update() {
            pelletsEaten = 0;
            eaten = false;
            if (isFruit) {
                fruitWorth = 100;
            }
        }

        public void isBEaten() throws FileNotFoundException {
            if (!eaten && dist(x, y, pacman.x, pacman.y) < cellWidth / 8f + pacman.size / 8f) {
                if (isFruit) {
                    switch (fruitType) {
                        case "cherry" -> fruitWorth = 100;
                        case "strawberry" -> fruitWorth = 300;
                        case "orange" -> fruitWorth = 500;
                        case "apple" -> fruitWorth = 700;
                        case "melon" -> fruitWorth = 1000;
                        case "galaxian" -> fruitWorth = 2000;
                        case "bell" -> fruitWorth = 3000;
                        case "key" -> fruitWorth = 5000;
                    }
                    score += fruitWorth;
                    fruit.play();
                } else {
                    if (!pelletFirst) {
                        dotSound2.play();
                        pelletFirst = true;
                    } else {
                        dotSound1.play();
                        pelletFirst = false;
                    }
                    score += pelletWorth;
                    pelletsEaten++;
                }
                eaten = true;
                giveLives();
                messages = splice(messages, "Your score is:" + str(score), 0);
                increaseHighScore();
            }
        }

        public void draw() {
            if (!eaten) {
                noStroke();
                if (isFruit) {
                    switch (fruitType) {
                        case "strawberry" -> image(strawberry, x, y, cellWidth, cellWidth);
                        case "orange" -> image(orange, x, y, cellWidth, cellWidth);
                        case "apple" -> image(apple, x, y, cellWidth, cellWidth);
                        case "melon" -> image(melon, x, y, cellWidth, cellWidth);
                        case "galaxian" -> image(galaxian, x, y, cellWidth, cellWidth);
                        case "bell" -> image(bell, x, y, cellWidth, cellWidth);
                        case "key" -> image(keyI, x, y, cellWidth, cellWidth);
                        default -> image(cherry, x, y, cellWidth, cellWidth);
                    }
                } else {
                    noStroke();
                    fill(255, 150, 0);
                    ellipse(x, y, cellWidth / 2f - 1, cellWidth / 2f - 1);
                }
            }
        }

        public void goodPosition(int coordsX1, int coordsY1) {
            boolean a = false;
            if (coordsY1 == 1 && !isFruit) {
                if (coordsX1 == 1 || coordsX1 == 8) {
                    a = true;
                }
            }
            if (!cells[coordsX1][coordsY1].open || a) {
                update();
                x = createPosition(true);
                y = createPosition(false);
                while (dist(x, y, pacman.x, pacman.y) < cellWidth / 2f) {
                    x = createPosition(true);
                    y = createPosition(false);
                }
                fill(255, 128, 255);
                ellipse(x, y, cellWidth / 2f - 1, cellWidth / 2f - 1);
                pelletErrors++;
                messages = splice(messages, "Adjustment in Progress...", 0);
                messages = splice(messages, str(pelletErrors) + "pellet(s) were misplaced", 0);
                makePelletCoords();
            }
            if (isFruit) {
                y = Math.round(cellWidth * 1.5f);
            }
        }
    }

    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Pacman~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
    class Pacman {
        final int size = Math.round(cellWidth - 1);
        final float speed = pacmanSpeed * ((cellWidth + 32) / 2) / 32f;
        final int stopBuffer = 2;
        boolean stopped = true;
        float x = cellWidth / 2f + cellWidth;
        float y = cellWidth / 2f + cellWidth;

        String nextDir = "stopped", dir = "stopped", lastDir = "stopped";

        public void show(float mouthSize) {
            float mouthOpenTop, mouthOpenBottom;
            /*if (Float.isNaN(mouthSize)) {
                mouthSize = 16;
            }*/
            if (stopped && !startsWMouth) {
                mouthSize = 0;
                if (playStartSound) {
                    startSound.play();
                    playStartSound = false;
                }
            }
            fill(255, 255, 0);
            translate(x, y);
            switch (lastDir) {
                case "up" -> rotate(PI + HALF_PI);
                case "down" -> rotate(HALF_PI);
                case "left" -> rotate(PI);
            }
            mouthOpenTop = map(mouthSize, 0, 60, 0, 0.52f);
            mouthOpenBottom = map(mouthSize, 0, 60, TWO_PI, 5.76f);
            arc(0, 0, (cellWidth * 15) / 16f, (cellWidth * 15) / 16f, mouthOpenTop, mouthOpenBottom);
            resetMatrix();
        }

        public void update() {
            //StackTraceElement[] stacktrace = Thread.currentThread().getStackTrace();
            //maybe this number needs to be corrected
            //println((frameCount/2)%6+": "+stacktrace[frameCount%6].getMethodName());
            coordsX = Math.round(x / cellWidth + 0.5f) - 1;
            coordsY = Math.round(y / cellWidth + 0.5f) - 1;
            if (pelletsEaten >= pellet.length - 1) {
                for (Pellet value : pellet) {
                    value.update();
                }
                first = false;
                coordsX = 1;
                coordsY = 1;
                x = cellWidth * 1.5f;
                y = cellWidth * 1.5f;
                dir = "stopped";
                nextDir = "stopped";
            }
            switch (nextDir) {
                case "up" -> {
                    if (cells[coordsX][coordsY - 1].open) {
                        dir = nextDir;
                        x = coordsX * cellWidth + (cellWidth / 2f);
                    }/* else {
                        if (other && !nextDir.equals(dir)) {
                            messages = splice(messages, "I want to move up", 0);
                        }
                    }*/
                }
                case "down" -> {
                    if (cells[coordsX][coordsY + 1].open) {
                        dir = nextDir;
                        x = coordsX * cellWidth + (cellWidth / 2f);
                    }/* else {
                        if (other && !nextDir.equals(dir)) {
                            messages = splice(messages, "I want to move down", 0);
                        }
                    }*/
                }
                case "right" -> {
                    if (cells[coordsX + 1][coordsY].open) {
                        dir = nextDir;
                        y = coordsY * cellWidth + (cellWidth / 2f);
                    }/* else {
                        if (other && !nextDir.equals(dir)) {
                            messages = splice(messages, "I want to move right", 0);
                        }
                    }*/
                }
                case "left" -> {
                    if (cells[coordsX - 1][coordsY].open) {
                        dir = nextDir;
                        y = coordsY * cellWidth + (cellWidth / 2f);
                    }/* else {
                        if (other && !nextDir.equals(dir)) {
                            messages = splice(messages, "I want to move left", 0);
                        }
                    }*/
                }
            }


            switch (dir) {
                case "up" -> {
                    if (cells[coordsX][coordsY - 1].open) {
                        y -= speed;
                        stopped = false;
                        if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                            x += speed;
                        }
                        if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                            x -= speed;
                        }
                        lastDir = "up";
                    } else {
                        if (y <= (coordsY * cellWidth + (cellWidth / 2f)) + stopBuffer) {
                            dir = nextDir;
                            nextDir = "stopped";
                        } else {
                            y -= speed;
                        }
                    }

                }
                case "down" -> {
                    if (cells[coordsX][coordsY + 1].open) {
                        y += speed;
                        stopped = false;
                        if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                            x += speed;
                        }
                        if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                            x -= speed;
                        }
                        lastDir = "down";
                    } else {
                        if (y >= (coordsY * cellWidth + (cellWidth / 2f)) - stopBuffer) {
                            dir = nextDir;
                            nextDir = "stopped";
                        } else {
                            y += speed;
                        }
                    }

                }
                case "right" -> {

                    if (cells[coordsX + 1][coordsY].open) {
                        x += speed;
                        stopped = false;
                        if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                            y += speed;
                        }
                        if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                            y -= speed;
                        }
                        lastDir = "right";
                    } else {
                        if (x >= (coordsX * cellWidth + (cellWidth / 2f)) - stopBuffer) {
                            dir = nextDir;
                            nextDir = "stopped";
                        } else {
                            x += speed;
                        }
                    }
                }
                case "left" -> {

                    if (cells[coordsX - 1][coordsY].open) {
                        x -= speed;

                        stopped = false;
                        if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                            y += speed;
                        }
                        if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                            y -= speed;
                        }
                        lastDir = "left";
                    } else {
                        if (x <= (coordsX * cellWidth + (cellWidth / 2f)) + stopBuffer) {
                            dir = nextDir;
                            nextDir = "stopped";
                        } else {
                            x -= speed;
                        }
                    }
                }
                case "stopped" -> {
                    if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                        x += speed;
                    }
                    if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                        x -= speed;
                    }
                    if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                        y += speed;
                    }
                    if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                        y -= speed;
                    }
                }
            }
            if (x > 400) {
                x = cellWidth + size / 2f;
            }
            if (y > 400) {
                y = cellWidth + size / 2f;
            }
            coordsX = Math.round(x / cellWidth + 0.5f) - 1;
            coordsY = Math.round(y / cellWidth + 0.5f) - 1;
        }

        public void up() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX][coordsY - 1].open) {
                dir = "up";
                nextDir = "up";
                if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                    x += speed;
                }
                if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                    x -= speed;
                }
            } else {
                nextDir = "up";
            }

            // }
        }

        public void down() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX][coordsY + 1].open) {
                dir = "down";
                nextDir = "down";
                if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                    x += speed;
                }
                if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                    x -= speed;
                }
            } else {
                nextDir = "down";
            }


        }

        public void right() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX + 1][coordsY].open) {
                dir = "right";
                nextDir = "right";
                if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                    y += speed;
                }
                if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                    y -= speed;
                }
            } else {
                nextDir = "right";
            }


        }

        public void left() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX - 1][coordsY].open) {
                dir = "left";
                nextDir = "left";
                if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                    y += speed;
                }
                if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                    y -= speed;
                }
            } else {
                nextDir = "left";
            }

        }


        public void stop() {
            dir = "stopped";
            stopped = false;
            nextDir = "stopped";
        }
    }

    class Pixel {
        static int size;
        final int colourCode;
        final float x;
        final float y;
        int colour;

        Pixel(int colourCode1, float x1, float y1) {
            colourCode = colourCode1;
            size = 2;
            x = x1;
            y = y1;
        }

        public void colourInit() {
            switch (colourCode) {
                case 0 -> colour = color(255, 0);
                case 2, 3 -> colour = color(255);
                default -> colour = color(255, 0, 0);
            }
        }


        public void draw(int gColour) {
            if (colourCode == 1) {
                colour = gColour;
            }
            rectMode(CENTER);
            fill(colour);
            rect(x, y, size, size);
            rectMode(CORNER);
        }
    }
