    private boolean checkGoodDir(String dir, int posX, int posY) {
        boolean goodDir = true;
        switch (dir) {
            case "up" -> {
                if (posY - 1 >= 0) {
                    goodDir = cells[posX][posY - 1].open;
                }
            }
            case "down" -> {
                if (posY + 1 <= height / cellWidth) {
                    goodDir = cells[posX][posY + 1].open;
                }
            }
            case "right" -> {
                if (posX + 1 <= width / cellWidth) {
                    goodDir = cells[posX + 1][posY].open;
                }
            }
            case "left" -> {
                if (posX - 1 >= 0) {
                    goodDir = cells[posX - 1][posY].open;
                }
            }
        }
        return !goodDir;
    }


