    //// Pellet \\\\
    final class Pellet {
        private int x;
        private int y;
        private boolean eaten = false;
        private boolean isFruit = false;
        private String fruitType = "cherry";

        private Pellet(int x1, int y1) {
            x = x1;
            y = y1;
        }

        void update() {
            pelletsEaten = 0;
            eaten = false;
            if (isFruit) {
                fruitWorth = 100;
            }
        }

        private void isBEaten() throws FileNotFoundException {
            if (!eaten && dist(x, y, pacman.x, pacman.y) < cellWidth / 8f + Pacman.size / 8f) {
                if (isFruit) {
                    switch (fruitType) {
                        case "cherry" -> fruitWorth = 100;
                        case "strawberry" -> fruitWorth = 300;
                        case "orange" -> fruitWorth = 500;
                        case "apple" -> fruitWorth = 700;
                        case "melon" -> fruitWorth = 1000;
                        case "galaxian" -> fruitWorth = 2000;
                        case "bell" -> fruitWorth = 3000;
                        case "key" -> fruitWorth = 5000;
                    }
                    score += fruitWorth;
                    fruit.play();
                } else {
                    if (!pelletFirst) {
                        dotSound2.play();
                        pelletFirst = true;
                    } else {
                        dotSound1.play();
                        pelletFirst = false;
                    }
                    score += pelletWorth;
                    pelletsEaten++;
                }
                eaten = true;
                giveLives();
                messages = splice(messages, "Your score is:" + str(score), 0);
                increaseHighScore();
            }
        }

        private void draw() {
            if (!eaten) {
                noStroke();
                if (isFruit) {
                    switch (fruitType) {
                        case "strawberry" -> image(strawberry, x, y, cellWidth, cellWidth);
                        case "orange" -> image(orange, x, y, cellWidth, cellWidth);
                        case "apple" -> image(apple, x, y, cellWidth, cellWidth);
                        case "melon" -> image(melon, x, y, cellWidth, cellWidth);
                        case "galaxian" -> image(galaxian, x, y, cellWidth, cellWidth);
                        case "bell" -> image(bell, x, y, cellWidth, cellWidth);
                        case "key" -> image(keyI, x, y, cellWidth, cellWidth);
                        default -> image(cherry, x, y, cellWidth, cellWidth);
                    }
                } else {
                    noStroke();
                    fill(255, 150, 0);
                    ellipse(x, y, cellWidth / 2f - 1, cellWidth / 2f - 1);
                }
            }
        }

        private void goodPosition(int coordsX1, int coordsY1) {
            boolean a = false;
            if (coordsY1 == 1 && !isFruit) {
                if (coordsX1 == 1 || coordsX1 == 8) {
                    a = true;
                }
            }
            if (!cells[coordsX1][coordsY1].open || a) {
                update();
                x = createPosition(true);
                y = createPosition(false);
                while (dist(x, y, pacman.x, pacman.y) < cellWidth / 2f) {
                    x = createPosition(true);
                    y = createPosition(false);
                }
                fill(255, 128, 255);
                ellipse(x, y, cellWidth / 2f - 1, cellWidth / 2f - 1);
                pelletErrors++;
                messages = splice(messages, "Adjustment in Progress...", 0);
                messages = splice(messages, str(pelletErrors) + "pellet(s) were misplaced", 0);
                makePelletCoords();
            }
            if (isFruit) {
                y = Math.round(cellWidth * 1.5f);
            }
        }
    }

    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Pacman~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
    final class Pacman {
        static final int size = Math.round(cellWidth - 1);
        private static final float speed = settings.pacmanSpeed * ((cellWidth + 32) / 2) / 32f;
        float x = cellWidth / 2f + cellWidth;
        float y = cellWidth / 2f + cellWidth;
        private boolean stopped = true;
        private String nextDir = "stopped";
        private String dir = "stopped";
        private String lastDir = "stopped";

        private void show(float mouthSize) {
            float mouthOpenTop, mouthOpenBottom;
            if (stopped && settings.startsAsCircle) {
                mouthSize = 0;
                if (playStartSound) {
                    startSound.play();
                    playStartSound = false;
                }
            }
            fill(255, 255, 0);
            translate(x, y);
            switch (lastDir) {
                case "up" -> rotate(PI + HALF_PI);
                case "down" -> rotate(HALF_PI);
                case "left" -> rotate(PI);
            }
            mouthOpenTop = map(mouthSize, 0, 60, 0, 0.52f);
            mouthOpenBottom = map(mouthSize, 0, 60, TWO_PI, 5.76f);
            arc(0, 0, (cellWidth * 15) / 16f, (cellWidth * 15) / 16f, mouthOpenTop, mouthOpenBottom);
            resetMatrix();
        }

        private void update() {
            if (settings.useClassicHitbox) {
                float offsetX = 0.2f;
                float offsetY = 0.1f;
                float a = 3;
                float b = 1;
                switch (dir) {
                    case "up" -> offsetY += cellWidth / a + b;
                    case "down" -> offsetY -= cellWidth / a + b;
                    case "right" -> offsetX -= cellWidth / a + b;
                    case "left" -> offsetX += cellWidth / a + b;
                }
                coordsX = round((x + offsetX) / cellWidth + 0.5f) - 1;
                coordsY = round((y + offsetY) / cellWidth + 0.5f) - 1;
            } else {
                coordsX = Math.round(x / cellWidth + 0.5f) - 1;
                coordsY = Math.round(y / cellWidth + 0.5f) - 1;
            }
            if (pelletsEaten >= pellet.length - 1) {
                for (Pellet value : pellet) {
                    value.update();
                }
                first = false;
                coordsX = 1;
                coordsY = 1;
                x = cellWidth * 1.5f;
                y = cellWidth * 1.5f;
                dir = "stopped";
                nextDir = "stopped";
            }

            switch (nextDir) {
                case "up" -> {
                    if (cells[coordsX][coordsY - 1].open) {
                        dir = nextDir;
                        x = coordsX * cellWidth + (cellWidth / 2f);
                    }
                }
                case "down" -> {
                    if (cells[coordsX][coordsY + 1].open) {
                        dir = nextDir;
                        x = coordsX * cellWidth + (cellWidth / 2f);
                    }
                }
                case "right" -> {
                    if (cells[coordsX + 1][coordsY].open) {
                        dir = nextDir;
                        y = coordsY * cellWidth + (cellWidth / 2f);
                    }
                }
                case "left" -> {
                    if (cells[coordsX - 1][coordsY].open) {
                        dir = nextDir;
                        y = coordsY * cellWidth + (cellWidth / 2f);
                    }
                }
            }
            if (settings.useClassicHitbox) {
                switch (dir) {
                    case "up" -> {
                        if (cells[parseInt(coordsX)][parseInt(coordsY) - 1].open) {
                            y -= speed;
                            stopped = false;
                            if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                                x++;
                            }
                            if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                                x--;
                            }
                            lastDir = "up";
                        } else {
                            dir = nextDir;
                            nextDir = "stopped";
                        }
                    }
                    case "down" -> {
                        if (cells[parseInt(coordsX)][parseInt(coordsY) + 1].open) {
                            y += speed;
                            stopped = false;
                            if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                                x++;
                            }
                            if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                                x--;
                            }
                            lastDir = "down";
                        } else {
                            dir = nextDir;
                            nextDir = "stopped";
                        }
                    }
                    case "right" -> {
                        if (cells[parseInt(coordsX) + 1][parseInt(coordsY)].open) {
                            x += speed;
                            stopped = false;
                            if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                                y++;
                            }
                            if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                                y--;
                            }
                            lastDir = "right";
                        } else {
                            dir = nextDir;
                            nextDir = "stopped";
                        }
                    }
                    case "left" -> {
                        if (cells[parseInt(coordsX) - 1][parseInt(coordsY)].open) {
                            x -= speed;
                            stopped = false;
                            if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                                y++;
                            }
                            if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                                y--;
                            }
                            lastDir = "left";
                        } else {
                            dir = nextDir;
                            nextDir = "stopped";
                        }
                    }
                    case "stopped" -> {
                        if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                            x++;
                        }
                        if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                            x--;
                        }
                        if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                            y++;
                        }
                        if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                            y--;
                        }
                    }
                }

            } else {


                int stopBuffer = 2;
                switch (dir) {
                    case "up" -> {
                        if (cells[coordsX][coordsY - 1].open) {
                            y -= speed;
                            stopped = false;
                            if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                                x += speed;
                            }
                            if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                                x -= speed;
                            }
                            lastDir = "up";
                        } else {
                            if (y <= (coordsY * cellWidth + (cellWidth / 2f)) + stopBuffer) {
                                dir = nextDir;
                                nextDir = "stopped";
                            } else {
                                y -= speed;
                            }
                        }
                    }
                    case "down" -> {
                        if (cells[coordsX][coordsY + 1].open) {
                            y += speed;
                            stopped = false;
                            if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                                x += speed;
                            }
                            if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                                x -= speed;
                            }
                            lastDir = "down";
                        } else {
                            if (y >= (coordsY * cellWidth + (cellWidth / 2f)) - stopBuffer) {
                                dir = nextDir;
                                nextDir = "stopped";
                            } else {
                                y += speed;
                            }
                        }
                    }
                    case "right" -> {
                        if (cells[coordsX + 1][coordsY].open) {
                            x += speed;
                            stopped = false;
                            if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                                y += speed;
                            }
                            if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                                y -= speed;
                            }
                            lastDir = "right";
                        } else {
                            if (x >= (coordsX * cellWidth + (cellWidth / 2f)) - stopBuffer) {
                                dir = nextDir;
                                nextDir = "stopped";
                            } else {
                                x += speed;
                            }
                        }
                    }
                    case "left" -> {
                        if (cells[coordsX - 1][coordsY].open) {
                            x -= speed;
                            stopped = false;
                            if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                                y += speed;
                            }
                            if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                                y -= speed;
                            }
                            lastDir = "left";
                        } else {
                            if (x <= (coordsX * cellWidth + (cellWidth / 2f)) + stopBuffer) {
                                dir = nextDir;
                                nextDir = "stopped";
                            } else {
                                x -= speed;
                            }
                        }
                    }
                    case "stopped" -> {
                        if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                            x += speed;
                        }
                        if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                            x -= speed;
                        }
                        if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                            y += speed;
                        }
                        if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                            y -= speed;
                        }
                    }
                }
            }

            if (x > 400) {
                x = cellWidth + size / 2f;
            }
            if (y > 400) {
                y = cellWidth + size / 2f;
            }
            coordsX = Math.round(x / cellWidth + 0.5f) - 1;
            coordsY = Math.round(y / cellWidth + 0.5f) - 1;
        }

        private void up() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX][coordsY - 1].open) {
                dir = "up";
                nextDir = "up";
                if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                    x += speed;
                }
                if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                    x -= speed;
                }
            } else {
                nextDir = "up";
            }

            // }
        }

        private void down() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX][coordsY + 1].open) {
                dir = "down";
                nextDir = "down";
                if (x < coordsX * cellWidth + (cellWidth / 2f)) {
                    x += speed;
                }
                if (x > coordsX * cellWidth + (cellWidth / 2f)) {
                    x -= speed;
                }
            } else {
                nextDir = "down";
            }


        }

        private void right() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX + 1][coordsY].open) {
                dir = "right";
                nextDir = "right";
                if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                    y += speed;
                }
                if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                    y -= speed;
                }
            } else {
                nextDir = "right";
            }


        }

        private void left() {
            coordsX = Math.round((x) / cellWidth + 0.5f) - 1;
            coordsY = Math.round((y) / cellWidth + 0.5f) - 1;
            if (cells[coordsX - 1][coordsY].open) {
                dir = "left";
                nextDir = "left";
                if (y < coordsY * cellWidth + (cellWidth / 2f)) {
                    y += speed;
                }
                if (y > coordsY * cellWidth + (cellWidth / 2f)) {
                    y -= speed;
                }
            } else {
                nextDir = "left";
            }

        }


        private void stop() {
            dir = "stopped";
            stopped = false;
            nextDir = "stopped";
        }
    }

    private final class Pixel {
        private static int size;
        private final int colourCode;
        private final float x;
        private final float y;
        private int colour;

        private Pixel(int colourCode1, float x1, float y1) {
            colourCode = colourCode1;
            size = 2;
            x = x1;
            y = y1;
        }

        private void colourInit() {
            switch (colourCode) {
                case 0 -> colour = color(255, 0);
                case 2, 3 -> colour = color(255);
                default -> colour = color(255, 0, 0);
            }
        }


        private void draw(int gColour) {
            if (colourCode == 1) {
                colour = gColour;
            }
            rectMode(CENTER);
            fill(colour);
            rect(x, y, size, size);
            rectMode(CORNER);
        }
    }
